SELECT PRODUCT_ID, SELLER_ID, PRICE, DESCRIPTION, QUALITY_CONDITION, CREATED_AT, UPDATED_AT, READ_COUNT, STATUS, TITLE, IMG_ID FROM PRODUCT
CATEGORY LIKE CONCAT('%', #{searchWord}, '%') OR TITLE LIKE CONCAT('%', #{searchWord}, '%') OR DESCRIPTION LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY CREATED_AT DESC LIMIT #{startProductIdx}, #{onePageProductCnt}
